void config( int32_t *in)
{

    for(int i = 0; i < 4; i++){
        cgra_input[0][i] = &(filter[(4*i)][0][0][0]);
        cgra_input[1][i] = &(filter[(4*i)+1][0][0][0]);
        cgra_input[2][i] = &(filter[(4*i)+2][0][0][0]);
        cgra_input[3][i] = &(filter[(4*i)+3][0][0][0]);
    }

    for(int i = 4; i < 8; i++){
        cgra_input[0][i] = &(in[0]);
        cgra_input[1][i] = &(in[0]);
        cgra_input[2][i] = &(in[0]);
        cgra_input[3][i] = &(in[0]);
    }

}



